mapper.select:
/* NoticeBoard.selectNoticeBoardList */ SELECT S2.*, ( SELECT CMN_COM_NM FROM T_CMM_COM WHERE CMN_COM_IDX = S2.BRD_CMN_COM_IDX )AS BRD_CMN_COM_NM, ( SELECT ADM_MST_NM FROM T_ADM_MST WHERE ADM_MST_ID = BRD_NTS_REG_ID ) AS ADM_MST_NM FROM ( SELECT ROWNUM AS RNUM, S1.* FROM ( SELECT BRD_NTS_IDX, BRD_NTS_TITLE, BRD_NTS_CONTENTS, BRD_NTS_TOP_YN, NVL(BRD_NTS_READ_CNT, 0) AS BRD_NTS_READ_CNT, BRD_NTS_USE_YN, BRD_NTS_REG_DT, BRD_NTS_REG_IP, BRD_NTS_REG_ID, BRD_NTS_UPD_DT, BRD_NTS_UPD_IP, BRD_NTS_UPD_ID, BRD_CMN_COM_IDX FROM T_BRD_NTS WHERE BRD_NTS_TOP_YN = #{BRD_NTS_TOP_YN}
ORDER BY BRD_NTS_REG_DT DESC ) S1 ) S2
WHERE S2.RNUM BETWEEN #{startNum} AND #{endNum}
