mapper.select:
/* BannerDisplay.selectBannerDisplayList */ SELECT BM.BNR_MST_IDX , BM.BNR_MST_TITLE , BM.CMN_COM_IDX , BM.BNR_MST_ST_DT , BM.BNR_MST_ST_HH , BM.BNR_MST_ST_MM , BM.BNR_MST_ED_DT , BM.BNR_MST_ED_HH , BM.BNR_MST_ED_MM , BM.BNR_MST_USE_YN , BM.BNR_MST_REG_DT , BM.BNR_MST_REG_IP , BM.BNR_MST_REG_ID , BM.BNR_MST_SRT , BM.BNR_MST_USE_YN , BM.BNR_MST_TGT , BM.BNR_MST_URL , BM.BND_MST_IDX , BM.ADM_MST_ID , (SELECT BND_MST_BRAND_NM FROM T_BND_MST WHERE BND_MST_IDX = BM.BND_MST_IDX) AS BND_MST_NM , (SELECT ADM_MST_NM FROM T_ADM_MST WHERE ADM_MST_ID = BM.ADM_MST_ID) AS ADM_MST_NM , TO_DATE(TO_CHAR(TO_DATE(BM.BNR_MST_ED_DT,'YYYYMMDDHH24MISS'),'YYYYMMDD'),'YYYYMMDD') - TO_DATE(TO_CHAR(SYSDATE, 'YYYYMMDD'),'YYYYMMDD') +1 AS BNR_MST_MOD_DT , CASE WHEN BM.BNR_MST_USE_YN = 'Y' AND TO_CHAR(SYSDATE,'YYYYMMDDYYYYMMDDHH24MISS') BETWEEN BM.BNR_MST_ST_DT||BM.BNR_MST_ST_HH||BM.BNR_MST_ST_MM AND BM.BNR_MST_ED_DT||BM.BNR_MST_ED_HH||BM.BNR_MST_ED_MM THEN 'ON' WHEN BM.BNR_MST_USE_YN = 'Y' AND TO_CHAR(SYSDATE,'YYYYMMDDYYYYMMDDHH24MISS') < BM.BNR_MST_ED_DT||BM.BNR_MST_ED_HH||BM.BNR_MST_ED_MM THEN '대기중' ELSE 'OFF' END AS BNR_MST_STATE , BNR_MST_HEX_CD FROM T_BNR_MST BM WHERE CMN_COM_IDX = #{CMN_COM_IDX}
AND BM.BNR_MST_ENV = #{searchGubun}
AND BM.BND_MST_IDX = #{BND_MST_IDX}
AND BM.ADM_MST_ID = #{ADM_MST_ID}
ORDER BY BM.BNR_MST_SRT ASC

mapper.insert:
SELECT SEQ_BNR_MST_IDX.NEXTVAL AS BNR_MST_IDX FROM DUAL
/* BannerDisplay.insertBannerDisplay */ INSERT INTO T_BNR_MST ( BNR_MST_IDX, BNR_MST_TITLE, CMN_COM_IDX, BNR_MST_ST_DT, BNR_MST_ST_HH, BNR_MST_ST_MM, BNR_MST_ED_DT, BNR_MST_ED_HH, BNR_MST_ED_MM, BNR_MST_USE_YN, BNR_MST_REG_DT, BNR_MST_REG_IP, BNR_MST_REG_ID, BNR_MST_ENV, BNR_MST_GBN, BNR_MST_TGT, BNR_MST_URL, BNR_MST_SRT, BNR_MST_HEX_CD
,BND_MST_IDX
,ADM_MST_ID
, BNR_MST_SUB_TITLE ) VALUES ( #{BNR_MST_IDX}, #{BNR_MST_TITLE}, #{CMN_COM_IDX}, REPLACE(#{BNR_MST_ST_DT},'-',''), #{BNR_MST_ST_HH}, #{BNR_MST_ST_MM}, REPLACE(#{BNR_MST_ED_DT},'-',''), #{BNR_MST_ED_HH}, #{BNR_MST_ED_MM}, NVL(#{BNR_MST_USE_YN}, 'N'), TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS'), #{BNR_MST_REG_IP}, #{BNR_MST_REG_ID}, #{PRD_CTG_ENV}, #{BNR_MST_GBN}, #{BNR_MST_TGT}, #{BNR_MST_URL}, 1, #{BNR_MST_HEX_CD}
,#{BND_MST_IDX}
,#{ADM_MST_ID}
, #{BNR_MST_SUB_TITLE} )

mapper.update:
/* BannerDisplay.updateBannerDisplaySort */
UPDATE
    T_BNR_MST 
SET
    BNR_MST_SRT = BNR_MST_SRT + 1
WHERE
    CMN_COM_IDX = #{CMN_COM_IDX}
AND
    BNR_MST_IDX  <>  #{BNR_MST_IDX}
