mapper.select:
/* CategoryProduct.selectCategoryProductList */ SELECT PRD_CTG_IDX , PRD_CTG_NM , PRD_CTG_UP_IDX , PRD_CTG_LEVEL FROM T_PRD_CTG WHERE PRD_CTG_DEL_YN != 'Y'
AND PRD_CTG_USE_YN = 'Y'
AND PRD_CTG_ENV = #{PRD_CTG_ENV}
START WITH PRD_CTG_UP_IDX = -1 CONNECT BY PRIOR PRD_CTG_IDX = PRD_CTG_UP_IDX ORDER SIBLINGS BY PRD_CTG_SORT ASC

/* CategoryProduct.selectSubCategoryProductList */ SELECT PRD_CTG_IDX , PRD_CTG_NM , PRD_CTG_USE_YN FROM T_PRD_CTG WHERE PRD_CTG_UP_IDX = #{PRD_CTG_IDX} AND PRD_CTG_DEL_YN != 'Y'
AND PRD_CTG_USE_YN = 'Y'
AND PRD_CTG_ENV = #{PRD_CTG_ENV}
AND PRD_CTG_DSP_YN = 'Y'
ORDER BY PRD_CTG_SORT ASC

/* CategoryProduct.selectUpperCategoryProductList */ SELECT PRD_CTG_IDX , PRD_CTG_NM , PRD_CTG_UP_IDX , PRD_CTG_LEVEL FROM T_PRD_CTG WHERE 1=1
AND PRD_CTG_USE_YN = 'Y'
START WITH PRD_CTG_IDX = #{PRD_CTG_IDX} CONNECT BY PRD_CTG_IDX = PRIOR PRD_CTG_UP_IDX ORDER BY PRD_CTG_LEVEL, PRD_CTG_SORT

mapper.insert:

    SELECT SEQ_PRD_CTG_IDX.NEXTVAL FROM DUAL 


/* CategoryProduct.insertCategoryProduct */
INSERT INTO T_PRD_CTG (
    PRD_CTG_IDX
    , PRD_CTG_NM
    , PRD_CTG_LEVEL
    , PRD_CTG_UP_IDX
    , PRD_CTG_TOP_IDX
    , PRD_CTG_SORT
    , PRD_CTG_USE_YN
    , PRD_CTG_REG_DT
    , PRD_CTG_REG_IP
    , PRD_CTG_REG_ID
    , PRD_CTG_UPD_DT
    , PRD_CTG_UPD_IP
    , PRD_CTG_UPD_ID
    , PRD_CTG_ENV
    , PRD_CTG_BRAND_CD
    , PRD_CTG_DEL_YN
    , PRD_CTG_DSP_YN
)VALUES (
    #{SEQ_PRD_CTG_IDX}
    , #{CHILD_CODENAME}
    , #{PRD_CTG_LEVEL}+1
    , #{PRD_CTG_IDX}
    , #{PRD_CTG_TOP_IDX}
    , (SELECT NVL((SELECT MAX(NVL(PRD_CTG_SORT, 0))+1 FROM T_PRD_CTG WHERE PRD_CTG_UP_IDX = #{PRD_CTG_IDX}), 1) AS C_SORT FROM DUAL)
    , NVL(#{PRD_CTG_USE_YN},'Y')
    , TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    , #{PRD_CTG_REG_IP}
    , #{PRD_CTG_REG_ID}
    , TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    , #{PRD_CTG_REG_IP}
    , #{PRD_CTG_REG_ID}
    , #{PRD_CTG_ENV}
    , #{PRD_CTG_BRAND_CD}
    , 'N'
    , 'Y'
)

mapper.update:

/* CategoryProduct.updateCategoryChildProduct */
UPDATE 
    T_PRD_CTG
SET
    PRD_CTG_DSP_YN = #{PRD_CTG_DSP_YN}
WHERE  
    (PRD_CTG_UP_IDX = #{PRD_CTG_IDX} OR PRD_CTG_TOP_IDX = #{PRD_CTG_IDX})
AND
    PRD_CTG_LEVEL > #{PRD_CTG_LEVEL}


/* CategoryProduct.updateCategoryChildUseProduct */
UPDATE 
    T_PRD_CTG
SET
    PRD_CTG_USE_YN = #{PRD_CTG_USE_YN}
WHERE  
    (PRD_CTG_UP_IDX = #{PRD_CTG_IDX} OR PRD_CTG_TOP_IDX = #{PRD_CTG_IDX})
AND
	PRD_CTG_LEVEL > #{PRD_CTG_LEVEL}


/* CategoryProduct.updateCategoryProductSort */
UPDATE 
    T_PRD_CTG
SET
    PRD_CTG_SORT = #{PRD_CTG_SORT},
    PRD_CTG_UPD_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS'),
    PRD_CTG_UPD_IP = #{PRD_CTG_UPD_IP},
    PRD_CTG_UPD_ID = #{PRD_CTG_UPD_ID}
WHERE  
    PRD_CTG_IDX = #{PRD_CTG_IDX}

mapper.insert:

    SELECT SEQ_DSP_SPC_IDX.NEXTVAL FROM DUAL 


/* CategoryProduct.insertCategoryProduct */
INSERT INTO T_DSP_SPC (
    DSP_SPC_IDX
    , DSP_PRD_CTG_IDX
    , DSP_PRD_SPC_IDX
    , DSP_PRD_DEL_YN
    , DSP_SPC_REG_ID
    , DSP_SPC_REG_DT
    , DSP_SPC_REG_IP
)VALUES (
    #{SEQ_DSP_SPC_IDX}
    , #{PRD_CTG_IDX}
    , #{DSP_PRD_SPC_IDX}
    , 'N'
    , #{PRD_CTG_REG_IP}
    , TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    , #{PRD_CTG_REG_ID}
)
