mapper.insert:

    SELECT SEQ_GFT_MST_IDX.NEXTVAL AS GFT_MST_IDX FROM DUAL

/* Gift.insertGift */
INSERT INTO T_GFT_MST (
                             GFT_MST_IDX
                           , GFT_MST_BRAND_CD
                           , GFT_MST_NAME
                           , GFT_MST_OPTION
                           , GFT_MST_ST_DT
                           , GFT_MST_ED_DT
                           , GFT_MST_COND_AMT
                           , GFT_MST_USE_YN
                           , GFT_MST_REG_DT
                           , GFT_MST_REG_IP
                           , GFT_MST_REG_ID
                           , GFT_MST_GUBUN
                           , GFT_MST_STOCK_CNT
                           , GFT_MST_PRD_TYPE
                           , GFT_MST_PRD_CD
                           , GFT_MST_PLN_IDX
              ) VALUES ( 
                            #{GFT_MST_IDX}
                           , #{GFT_MST_BRAND_CD}
                           , #{GFT_MST_NAME}
                           , #{GFT_MST_OPTION}
                           , REPLACE(#{GFT_MST_ST_DT},'-', '')
                           , REPLACE(#{GFT_MST_ED_DT},'-', '')
                           , #{GFT_MST_COND_AMT}
                           , #{GFT_MST_USE_YN}
                           , TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
                           , #{GFT_MST_REG_IP}
                           , #{GFT_MST_REG_ID}
                           , #{GFT_MST_GUBUN}
                           , #{GFT_MST_STOCK_CNT}
                           , #{GFT_MST_PRD_TYPE}
                           , #{GFT_MST_PRD_CD}
                           , #{GFT_MST_PLN_IDX}
                       )


/* Gift.insertProduct */
INSERT INTO T_PRD_GFT (
    PRD_GFT_IDX, PRD_PRD_MST_CD, GFT_MST_IDX
)VALUES (
    SEQ_PRD_GFT_IDX.NEXTVAL, #{PRD_PRD_MST_CD}, #{GFT_MST_IDX}
)
