mapper.select:
/* BanWordManager.selectBanWordManagerList */ SELECT S2.* FROM ( SELECT ROWNUM AS RNUM, S1.* FROM ( SELECT BAN_WRD_IDX, BAN_WRD_TXT, BAN_WRD_USE_YN, BAN_WRD_REG_DT, BAN_WRD_REG_ID FROM T_BAN_WRD WHERE 1=1
AND BAN_WRD_USE_YN = #{banWrdUserYn}
ORDER BY BAN_WRD_REG_DT DESC ) S1 ) S2
WHERE S2.RNUM BETWEEN #{startNum} AND #{endNum}

		/* BanWordManager.selectBanWordManagerTxtCheckCount */
		SELECT
			COUNT(1)
		FROM
			T_BAN_WRD
		WHERE
			BAN_WRD_TXT = #{BAN_WRD_TXT}

                AND BAN_WRD_IDX != #{BAN_WRD_IDX}

