mapper.select:
/* BasicManager.selectBasicManagerIdCheckCount */
SELECT
	COUNT(1)
FROM T_ADM_MST
WHERE
	ADM_MST_ID = #{ADM_MST_ID}

mapper.update:
/* BasicManager.updateBasicManager */ UPDATE T_ADM_MST SET
ADM_MST_PWD = #{ADM_MST_PWD},
ADM_MST_NM = #{ADM_MST_NM}, ADM_MST_STATE = NVL(#{ADM_MST_STATE}, 'N'), ADM_MST_TYPE = #{ADM_MST_TYPE}, ADM_MST_UPD_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS'), ADM_MST_UPD_IP = #{ADM_MST_UPD_IP}, ADM_MST_UPD_ID = #{ADM_MST_UPD_ID}, AUT_MST_IDX = #{AUT_MST_IDX}, ADM_MST_SUB_IDX = NVL(#{ADM_MST_SUB_IDX},-1), /* 입점업체 없는 경우 -1 등록 */ ADM_MST_ERP_NO = #{ADM_MST_ERP_NO} WHERE ADM_MST_ID = #{ADM_MST_ID}
