mapper.select:
/* CommonCodeManager.selectCommonCodeManagerList */ SELECT S2.* FROM ( SELECT ROWNUM AS RNUM, S1.* FROM ( SELECT CMN_COM_IDX, CMN_COM_NM, CMN_COM_LEVEL, CMN_COM_UP_IDX, CMN_COM_TOP_IDX, CMN_COM_ETC1, CMN_COM_ETC2, CMN_COM_ETC3, CMN_COM_SORT, CMN_COM_USE_YN, CMN_COM_REG_DT, CMN_COM_REG_IP, CMN_COM_REG_ID, CMN_COM_UPD_DT, CMN_COM_UPD_IP, CMN_COM_UPD_ID FROM T_CMM_COM WHERE 1=1
START WITH CMN_COM_TOP_IDX = 0 CONNECT BY PRIOR CMN_COM_IDX = CMN_COM_UP_IDX ORDER SIBLINGS BY CMN_COM_LEVEL, CMN_COM_SORT ASC ) S1 ) S2
WHERE S2.RNUM BETWEEN #{startNum} AND #{endNum}

mapper.update:
/* CommonCodeManager.updateCommonCodeManagerSortArray */
UPDATE
    T_CMM_COM
SET
    CMN_COM_SORT = #{CMN_COM_SORT},
    CMN_COM_UPD_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS'),
    CMN_COM_UPD_IP = #{CMN_COM_UPD_IP},
    CMN_COM_UPD_ID = #{CMN_COM_UPD_ID}
WHERE
    CMN_COM_IDX = #{CMN_COM_IDX}
