mapper.select:
/* MakerManager.selectMakerManagerList */ SELECT S2.* FROM ( SELECT ROWNUM AS RNUM, S1.* FROM ( SELECT MRK_MST_IDX, MRK_MST_BRAND_NM, MRK_MST_USE_YN, MRK_MST_REG_DT FROM T_MRK_MST WHERE 1=1
ORDER BY MRK_MST_REG_DT DESC ) S1 ) S2
WHERE S2.RNUM BETWEEN #{startNum} AND #{endNum}

mapper.update:
/* MakerManager.updateMakerManager */
UPDATE 
	T_MRK_MST
SET
	MRK_MST_USE_YN = NVL(#{MRK_MST_USE_YN},'Y'),
	MRK_MST_UPD_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS'),
	MRK_MST_UPD_ID = #{MRK_MST_UPD_ID},
	MRK_MST_UPD_IP = #{MRK_MST_UPD_IP}
WHERE
	MRK_MST_IDX = #{MRK_MST_IDX}
