mapper.update:
        /* custBgtByacctAprvl.savePopAprDetail 계정별 결재선 관리 상세 관리정보 수정 */
		UPDATE TB_CC_APRVL_SET_INFO
		   SET DEPT_ID = #{deptId}
		   	 , ACCT_ID = #{acctId}
		   	 , STR_AMT = DECODE(#{strAmt}, '', 0, NVL(#{strAmt}, 0))
		   	 , END_AMT = DECODE(#{endAmt}, '', 0, NVL(#{endAmt}, 0))
		   	 , UPDPSN_ID = #{oprtrId}
		   	 , UPD_DTM = SYSDATE
		 WHERE CO_CD = #{coCd}
		   AND BZPLC_ID = #{bzplcId}
		   AND APRVL_SPR_CD = #{aprvlSprCd}
		   AND APRVL_SET_SEQ = #{aprvlSetSeq}

        /* custBgtByacctAprvl.updatePopApr 계정별 결재선 관리 상세 결재선정보 수정 */
        MERGE INTO TB_CC_APRVL_SET_DTL
		USING DUAL 
		ON ( CO_CD = #{coCd}
		 AND BZPLC_ID = #{bzplcId}
		 AND APRVL_SPR_CD = #{aprvlSprCd}
		 AND APRVL_SET_SEQ = #{aprvlSetSeq}
		 AND APRV_SEQ = #{dgrCnt}
		)
		WHEN MATCHED THEN
		UPDATE SET APRVR_ID = #{mbrId}
		     , APRVL_PST_CD = #{aprvlPstCd}
		   	 , UPDPSN_ID = #{oprtrId}
		   	 , UPD_DTM = SYSDATE
		WHEN NOT MATCHED THEN
        INSERT(
        	CO_CD
		 	, BZPLC_ID 
		 	, APRVL_SPR_CD
		 	, APRVL_SET_SEQ
		 	, APRV_SEQ
			, APRVL_PST_CD
			, APRVR_ID
			, REGPSN_ID
			, REG_DTM
        )VALUES(
        	#{coCd}
        	, #{bzplcId}
        	, #{aprvlSprCd}
        	, #{aprvlSetSeq}
        	, #{dgrCnt}
        	, #{aprvlPstCd}
        	, #{mbrId}
        	, #{oprtrId}
        	, SYSDATE
		)

mapper.delete:
/* custBgtByacctAprvl.deletePopApr 계정별 결재선 관리 상세 결재선정보 Delete */
DELETE FROM TB_CC_APRVL_SET_DTL 
 WHERE CO_CD = #{coCd}
 AND BZPLC_ID = #{bzplcId}
 AND APRVL_SPR_CD = #{aprvlSprCd}
 AND APRVL_SET_SEQ = #{aprvlSetSeq}

mapper.insert:
        /* custBgtByacctAprvl.insertPopAprDetail 계정별 결재선 관리 상세 관리정보 생성 */
		INSERT INTO TB_CC_APRVL_SET_INFO(
        	CO_CD
		 	, BZPLC_ID 
		 	, DEPT_ID
		 	, APRVL_SPR_CD
		 	, APRVL_SET_SEQ
		   	, ACCT_ID
		   	, STR_AMT
		   	, END_AMT
		   	, USE_YN
			, REGPSN_ID
			, REG_DTM
        )VALUES(
        	#{coCd}
        	, #{bzplcId}
        	, #{deptId}
        	, #{aprvlSprCd}
        	, #{aprvlSetSeq}
        	, #{acctId}
        	, DECODE(#{strAmt}, '', 0, NVL(#{strAmt}, 0))
        	, DECODE(#{endAmt}, '', 0, NVL(#{endAmt}, 0))
        	, #{useYn}
        	, #{oprtrId}
        	, SYSDATE
		)

        /* custBgtByacctAprvl.insertPopApr 계정별 결재선 관리 상세 결재선정보 생성 */
		INSERT INTO TB_CC_APRVL_SET_DTL(
        	CO_CD
		 	, BZPLC_ID 
		 	, APRVL_SPR_CD
		 	, APRVL_SET_SEQ
		 	, APRV_SEQ
			, APRVL_PST_CD
			, APRVR_ID
			, REGPSN_ID
			, REG_DTM
        )VALUES(
        	#{coCd}
        	, #{bzplcId}
        	, #{aprvlSprCd}
        	, #{aprvlSetSeq}
        	, #{dgrCnt}
        	, #{aprvlPstCd}
        	, #{mbrId}
        	, #{oprtrId}
        	, SYSDATE
		)

mapper.select:
        /* custBgtByacctAprvl.selectAprvlSetSeq 생성된 계정별 결재선 관리 상세 관리정보 APRVL_SET_SEQ 채번 */
        SELECT NVL(MAX(APRVL_SET_SEQ)+1, 1)
		  FROM TB_CC_APRVL_SET_DTL
	     WHERE CO_CD = #{coCd}
		   AND BZPLC_ID = #{bzplcId}
		   AND APRVL_SPR_CD = '50'

        /* custBgtByacctAprvl.selectInsertValidationCheck  insert validation */
        SELECT COUNT(*) 
		  FROM TB_CC_APRVL_SET_INFO
		 WHERE CO_CD = #{coCd}
		  AND BZPLC_ID = #{bzplcId}
		  AND APRVL_SPR_CD = #{aprvlSprCd}
		  AND DEPT_ID = #{deptId}
		  AND ACCT_ID = #{acctId}

mapper.delete:
        /* custBgtByacctAprvl.deleteCustBgtByAcctAprv 계정별 결재선 관리 삭제 */
		DELETE FROM TB_CC_APRVL_SET_INFO
		 WHERE CO_CD = #{coCd}
		   AND BZPLC_ID = #{bzplcId}
		   AND APRVL_SPR_CD = #{aprvlSprCd}
		   AND APRVL_SET_SEQ = #{aprvlSetSeq}

/* custBgtByacctAprvl.deleteCustBgtByAcctAprvDetail 계정별 결재선 관리 결재선정보 삭제 */
DELETE FROM TB_CC_APRVL_SET_DTL 
 WHERE CO_CD = #{coCd}
 AND BZPLC_ID = #{bzplcId}
 AND APRVL_SPR_CD = #{aprvlSprCd}
 AND APRVL_SET_SEQ = #{aprvlSetSeq}
