mapper.select:
/* PrPerson.selectPersonList */ SELECT B.CO_CD, B.PRD_CLSF_CHRPSN_ID, PRD_CLSF_CHRPSN_SEQ, C.ORG_NM , C.EMP_NO , C.OPRTR_NM, A.PRD_CLCD, SSP_APP.FN_PR_FULL_CLSF_NM(A.CO_CD, A.PRD_CLCD) PRD_CLSF_NM , B.MALL_SPR_CD, B.PRD_CLSF_CHRPSN_TP_CD, B.PRD_CLSF_CHRPSN_USEYN, B.PRD_CLSF_CHRPSN_SPR_CD, B.UPD_RSN, TO_CHAR(B.REG_DTM, 'YYYY-MM-DD HH24:MI:SS') REG_DTM, (SELECT OPRTR_NM FROM SSP_APP.TB_CO_MBR_OPRTR_INFO OPR WHERE OPR.OPRTR_ID = B.REGPSN_ID) REGPSN_ID, TO_CHAR(B.UPD_DTM, 'YYYY-MM-DD HH24:MI:SS') UPD_DTM, (SELECT OPRTR_NM FROM SSP_APP.TB_CO_MBR_OPRTR_INFO OPR WHERE OPR.OPRTR_ID = B.UPDPSN_ID) UPDPSN_ID FROM SSP_APP.TB_PR_PRD_CLSF_INFO A INNER JOIN SSP_APP.TB_PR_PRD_CLSF_CHRPSN_INFO B ON A.CO_CD = B.CO_CD AND B.PRD_CLCD = A.PRD_CLCD LEFT OUTER JOIN SSP_APP.TB_CO_MBR_OPRTR_INFO C ON A.CO_CD = C.CO_CD AND B.PRD_CLSF_CHRPSN_ID = C.OPRTR_ID
ORDER BY ${SORT_COLUMN} ${SORT_TYPE}
ORDER BY B.CO_CD, B.PRD_CLCD, B.PRD_CLSF_CHRPSN_SPR_CD, B.REG_DTM
OFFSET #{START_NUM} ROWS FETCH FIRST #{ROW_COUNT} ROWS ONLY

mapper.sql:
WHERE A.CO_CD = #{CO_CD} AND A.LWST_CD_YN  = 'Y'
AND PRD_CLSF_CHRPSN_TP_CD = #{PRD_CLSF_CHRPSN_TP_CD}
AND B.MALL_SPR_CD = '10'  --SSP

    AND A.PRD_CLCD = #{PRD_CLCD}


    AND A.PRD_CLSF_NM LIKE '%'||#{PRD_CLSF_NM}||'%'


    AND B.PRD_CLSF_CHRPSN_USEYN = #{PRD_CLSF_CHRPSN_USEYN}



        AND B.PRD_CLSF_CHRPSN_ID = #{PSN_ID}


        AND B.REGPSN_ID = #{PSN_ID}


        AND B.UPDPSN_ID = #{PSN_ID}



    AND C.OPRTR_NM LIKE '%'||#{PSN_NM}||'%'


    AND C.ORG_CD = #{ORG_CD}



        AND B.REG_DTM


        AND B.UPD_DTM 

    BETWEEN TO_DATE(#{DTM_START}||'000000', 'YYYYMMDDHH24MISS') AND TO_DATE(#{DTM_END}||'235959', 'YYYYMMDDHH24MISS')



        AND B.PRD_CLSF_CHRPSN_SPR_CD = #{PRD_CLSF_CHRPSN_SPR_CD}

mapper.select:
/* PrPerson.selectPersonSprCd */
SELECT
    NVL(MAX('20'), '10') PRD_CLSF_CHRPSN_SPR_CD
FROM
    SSP_APP.TB_PR_PRD_CLSF_CHRPSN_INFO 
WHERE CO_CD = #{CO_CD}
    AND PRD_CLCD = #{PRD_CLCD}
    AND PRD_CLSF_CHRPSN_TP_CD = #{PRD_CLSF_CHRPSN_TP_CD}
    AND PRD_CLSF_CHRPSN_SPR_CD = '10'
    AND MNFR_NO = '*'

mapper.insert:
/* PrPerson.insertPerson */
SELECT SSP_APP.SQ_PR_PRD_CLSF_CHRPSN_INFO_01.NEXTVAL FROM DUAL
INSERT INTO SSP_APP.TB_PR_PRD_CLSF_CHRPSN_INFO (CO_CD, PRD_CLSF_CHRPSN_ID, PRD_CLCD, MNFR_NO, PRD_CLSF_CHRPSN_SEQ, MALL_SPR_CD, PRD_CLSF_CHRPSN_TP_CD, PRD_CLSF_CHRPSN_USEYN, PRD_CLSF_CHRPSN_SPR_CD, UPD_RSN, REG_DTM, REGPSN_ID) VALUES(#{CO_CD}, #{PRD_CLSF_CHRPSN_ID}, #{PRD_CLCD}, '*', #{PRD_CLSF_CHRPSN_SEQ}, #{MALL_SPR_CD}, #{PRD_CLSF_CHRPSN_TP_CD}, #{PRD_CLSF_CHRPSN_USEYN}, #{PRD_CLSF_CHRPSN_SPR_CD}, #{UPD_RSN}, SYSDATE, #{SESSION_OPRTR_ID})

mapper.select:
/* PrPerson.selectPersonDuplicateCheck */
SELECT
    COUNT(*)
FROM
    SSP_APP.TB_PR_PRD_CLSF_CHRPSN_INFO 
    WHERE CO_CD = #{CO_CD}
    AND PRD_CLCD = #{PRD_CLCD}
    AND PRD_CLSF_CHRPSN_ID = #{EMP_NO}
    AND MNFR_NO = '*'

mapper.update:
/* PrPerson.updatePerson */
UPDATE
    SSP_APP.TB_PR_PRD_CLSF_CHRPSN_INFO
SET
    PRD_CLSF_CHRPSN_USEYN = NVL(#{PRD_CLSF_CHRPSN_USEYN}, PRD_CLSF_CHRPSN_USEYN),

         PRD_CLSF_CHRPSN_ID = #{NEW_PRD_CLSF_CHRPSN_ID},

    UPD_RSN = #{UPD_RSN},
    UPD_DTM = SYSDATE,
    UPDPSN_ID = #{SESSION_OPRTR_ID}
WHERE
    CO_CD = #{CO_CD}
    AND PRD_CLSF_CHRPSN_ID = #{PRD_CLSF_CHRPSN_ID}
    AND PRD_CLCD = #{PRD_CLCD}
    AND PRD_CLSF_CHRPSN_SEQ = #{PRD_CLSF_CHRPSN_SEQ}
    AND MNFR_NO = '*'
