mapper.select:
/* DispPrdMngEstmReqLst.selectProductEstimateList | 전시상품 견적요청 목록 */ SELECT * FROM ( SELECT MST.* , CASE WHEN MST.ESTM_REQ_PROC_STATS_CD = '10' THEN '요청' ELSE (SELECT SSP_APP.FN_COM_DTL_CDNM('KO', 'ESTM_INFO_STATS_CD', MST.ESTM_INFO_STATS_CD) FROM DUAL) END AS ESTM_INFO_STATS_NM FROM ( SELECT '0' AS CHK , T1.CO_CD , T1.DISP_GRP_ID , T1.PRD_CLCD , T1.DISP_PRD_NM , FN_PR_FULL_CLSF_NM(T1.CO_CD, T1.PRD_CLCD) AS PRD_CLNM , LTRIM(T1.REPR_PRD_ID, '0') AS REPR_PRD_ID , T2.ESTM_REQ_NO , TO_CHAR(T2.REG_DTM, 'YYYY-MM-DD') AS REG_DTM , TO_CHAR(TO_DATE(T2.ESTM_REQDT, 'YYYY-MM-DD'), 'YYYY-MM-DD') AS ESTM_REQDT , ( CASE WHEN ( SELECT ESTM_REQ_PROC_STATS_CD FROM ( SELECT S1.SEQ, S1.ESTM_REQ_PROC_STATS_CD FROM TB_RD_RND_DISP_ESTM_STATS_DTLS S1 WHERE S1.CO_CD = T2.CO_CD AND S1.ESTM_REQ_NO = T2.ESTM_REQ_NO ORDER BY SEQ DESC ) WHERE ROWNUM = 1 ) = '70' THEN ( SELECT TO_CHAR(UPD_DTM, 'YYYY-MM-DD') FROM TB_RD_RND_DISP_ESTM_STATS_DTLS S1 WHERE S1.CO_CD = T2.CO_CD AND S1.ESTM_REQ_NO = T2.ESTM_REQ_NO AND S1.ESTM_REQ_PROC_STATS_CD = '70' ) ELSE TO_CHAR(TO_DATE(T2.ESTM_CMPL_DT, 'YYYY-MM-DD'), 'YYYY-MM-DD') END ) AS ESTM_CMPL_DT , TO_CHAR(TO_DATE(T2.ESTM_VLD_DT, 'YYYY-MM-DD'), 'YYYY-MM-DD') AS ESTM_VLD_DT , ( SELECT MALL_SPR_CD FROM ( SELECT S1.SEQ, S1.MALL_SPR_CD FROM TB_RD_RND_DISP_ESTM_STATS_DTLS S1 WHERE S1.CO_CD = T2.CO_CD AND S1.ESTM_REQ_NO = T2.ESTM_REQ_NO ORDER BY SEQ DESC ) WHERE ROWNUM = 1 ) AS MALL_SPR_CD , ( SELECT ESTM_REQ_PROC_STATS_CD FROM ( SELECT S1.SEQ, S1.ESTM_REQ_PROC_STATS_CD FROM TB_RD_RND_DISP_ESTM_STATS_DTLS S1 WHERE S1.CO_CD = T2.CO_CD AND S1.ESTM_REQ_NO = T2.ESTM_REQ_NO ORDER BY SEQ DESC ) WHERE ROWNUM = 1 ) AS ESTM_REQ_PROC_STATS_CD , CASE WHEN '20' >= T9.ESTM_INFO_STATS_CD AND T9.ESTM_CUNT > 0 THEN '30' ELSE T9.ESTM_INFO_STATS_CD END AS ESTM_INFO_STATS_CD , T3.MBR_ID , T3.MBR_NM , T3.BZPLC_ID , ( SELECT BZPLC_NM FROM TB_CC_BZPLC_BASIS S1 WHERE S1.CO_CD = T3.CO_CD AND S1.BZPLC_ID = T3.BZPLC_ID ) AS BZPLC_NM , T4.OPR_UNIT_ID , ( SELECT OPR_UNIT_NM FROM TB_CC_OPR_UNIT_BASIS S2 WHERE S2.CO_CD = T4.CO_CD AND S2.BZPLC_ID = T4.BZPLC_ID AND S2.OPR_UNIT_ID = T4.OPR_UNIT_ID ) AS OPR_UNIT_NM , TO_CHAR(TO_NUMBER(T7.PRD_ID)) AS NEW_PRD_ID , T5.NEW_PRD_REQ_NO , T5.MRO_REQ_NO , T6.OPRTR_ID , T6.OPRTR_NM , T6.ORG_CD , T6.ORG_NM , T7.MNFR_NO , T7.ORGPLC_CTRY_CD , T7.SELL_UNIT_CD , T7.BASIS_UNIT_CD , T7.BASIS_UNIT_QTY , T8.DOC_REG_ID AS PPI_DOC_REG_ID , T7.TX_CLCD , T7.HUB_HNL_DIS_TP_CD , ( SELECT LISTAGG(A.OPTN_DTL_NM,',') WITHIN GROUP ( ORDER BY A.SRT_SO ) FROM TB_RD_RND_DISP_OPTN_DTL A WHERE A.CO_CD = T1.CO_CD AND A.DISP_GRP_ID = T1.DISP_GRP_ID ) AS NEW_PRD_REQ_PRVL_LIST , T7.HUB_HNL_PSB_YN , T7.HRM_CHM_MTL_YN AS CHM_MTL_YN FROM TB_RD_RND_DISP_BASIS T1 , TB_RD_RND_DISP_ESTM_REQ_DTLS T2 , TB_CC_MBR_BASIS T3 , TB_CC_DEPT_BASIS T4 , TB_PR_NEW_PRD_REQ_PROC T5 , ( SELECT S1.CO_CD , S1.PRD_CLCD , S2.ORG_CD , S2.OPRTR_ID , S2.OPRTR_NM , S2.ORG_NM FROM TB_PR_PRD_CLSF_CHRPSN_INFO S1 , TB_CO_MBR_OPRTR_INFO S2 WHERE S1.CO_CD = S2.CO_CD AND S1.PRD_CLSF_CHRPSN_ID = S2.OPRTR_ID AND S1.PRD_CLSF_CHRPSN_TP_CD = '30' AND S1.PRD_CLSF_CHRPSN_SPR_CD = '10' AND S1.PRD_CLSF_CHRPSN_USEYN = 'Y' ) T6 , TB_PR_MRO_PRD_INFO T7 , TB_PR_PRD_INFO T8 , ( SELECT CO_CD , ESTM_REQ_NO , MAX(ESTM_DTL_STATS_CD) KEEP(DENSE_RANK LAST ORDER BY SEQ) ESTM_INFO_STATS_CD , MAX(UPDPSN_ID ) KEEP(DENSE_RANK LAST ORDER BY SEQ) UPDPSN_ID , MAX(UPD_DTM ) KEEP(DENSE_RANK LAST ORDER BY SEQ) UPD_DTM , ( SELECT COUNT(CASE WHEN ESTM_DTL_STATS_CD IN ('20', '40') THEN 1 END) ESTM_CUNT FROM TB_RD_REQ_CPRTCP_DISP_ESTM_DTL D WHERE D.CO_CD = K.CO_CD AND D.ESTM_REQ_NO = K.ESTM_REQ_NO ) ESTM_CUNT FROM TB_RD_REQ_CPRTCP_DISP_ESTM_STATS_DTLS K GROUP BY CO_CD, ESTM_REQ_NO ) T9 WHERE T1.CO_CD = T2.CO_CD AND T1.DISP_GRP_ID = T2.DISP_GRP_ID AND T1.PRD_CLCD = T2.PRD_CLCD AND T2.CO_CD = T9.CO_CD AND T2.ESTM_REQ_NO = T9.ESTM_REQ_NO AND T2.CO_CD = T3.CO_CD(+) AND T2.REGPSN_ID = T3.MBR_ID(+) AND T3.CO_CD = T4.CO_CD(+) AND T3.DEPT_ID = T4.DEPT_ID(+) AND T2.ESTM_REQ_NO = T5.ESTM_REQ_NO(+) AND T1.CO_CD = T6.CO_CD(+) AND T1.PRD_CLCD = T6.PRD_CLCD(+) AND T1.CO_CD = T7.CO_CD(+) AND T1.REPR_PRD_ID = T7.PRD_ID(+) AND T1.CO_CD = T8.CO_CD(+) AND T1.REPR_PRD_ID = T8.PRD_ID(+) AND T7.CO_CD = T5.CO_CD(+) AND T7.SSP_PRD_REG_REQ_ID = T5.NEW_PRD_REQ_NO(+) ) MST WHERE CO_CD = #{CO_CD}
AND EXISTS ( SELECT 'X' FROM TB_OD_ODR_INQ_TGT_DTLS A1 WHERE A1.SES_ID = #{SES_ID} AND A1.INQ_COND_DTLS = #{INQ_COND_DTLS} AND A1.COL_ITM = 'ESTM_REQ_NO'
AND ESTM_REQ_NO = A1.COND_DATA_VAL
AND MRO_REQ_NO = A1.COND_DATA_VAL
)
AND PRD_CLCD LIKE '%' || #{PRD_GRP_CD} || '%'
AND PRD_CLNM LIKE '%' || #{PRD_GRP_NM} || '%'
AND EXISTS ( SELECT 'X' FROM TB_OD_ODR_INQ_TGT_DTLS A1 WHERE A1.SES_ID = #{SES_ID} AND A1.INQ_COND_DTLS = #{INQ_COND_DTLS}
AND A1.COL_ITM = 'ESTM_BZP_NO' AND BZPLC_ID = A1.COND_DATA_VAL
AND A1.COL_ITM = 'ESTM_OPR_NO' AND OPR_UNIT_ID = A1.COND_DATA_VAL
)
AND EXISTS ( SELECT 'X' FROM TB_OD_ODR_INQ_TGT_DTLS A1 WHERE A1.SES_ID = #{SES_ID} AND A1.INQ_COND_DTLS = #{INQ_COND_DTLS}
AND A1.COL_ITM = 'ESTM_BZP_NM' AND REPLACE(BZPLC_NM, ' ', '') = A1.COND_DATA_VAL
AND A1.COL_ITM = 'ESTM_OPR_NM' AND REPLACE(OPR_UNIT_NM, ' ', '') = A1.COND_DATA_VAL
)
AND EXISTS ( SELECT 'X' FROM TB_OD_ODR_INQ_TGT_DTLS A1 WHERE A1.SES_ID = #{SES_ID} AND A1.INQ_COND_DTLS = #{INQ_COND_DTLS} AND A1.COL_ITM = 'ESTM_MBR_NO' AND MBR_ID = A1.COND_DATA_VAL )
AND EXISTS ( SELECT 'X' FROM TB_OD_ODR_INQ_TGT_DTLS A1 WHERE A1.SES_ID = #{SES_ID} AND A1.INQ_COND_DTLS = #{INQ_COND_DTLS} AND A1.COL_ITM = 'ESTM_MBR_NM' AND MBR_NM = A1.COND_DATA_VAL )
AND TO_DATE(REG_DTM, 'YYYY-MM-DD') >= TO_DATE(#{START_DTM}, 'YYYY-MM-DD')
AND TO_DATE(ESTM_CMPL_DT, 'YYYY-MM-DD') >= TO_DATE(#{START_DTM}, 'YYYY-MM-DD')
AND TO_DATE(REG_DTM, 'YYYY-MM-DD') <= TO_DATE(#{END_DTM}, 'YYYY-MM-DD')
AND TO_DATE(ESTM_CMPL_DT, 'YYYY-MM-DD') <= TO_DATE(#{END_DTM}, 'YYYY-MM-DD')
AND ESTM_REQ_PROC_STATS_CD = '10'
AND ESTM_REQ_PROC_STATS_CD != '10' AND ESTM_INFO_STATS_CD = #{PROC_STATS_CD}
AND ORG_CD LIKE '%' || #{TEAM_CD} || '%'
AND ORG_NM LIKE '%' || #{TEAM_NM} || '%'
AND OPRTR_ID LIKE '%' || #{CHAR_CD} || '%'
AND OPRTR_NM LIKE '%' || #{CHAR_NM} || '%'
AND DISP_PRD_NM LIKE '%' || #{PRD_NM} || '%'
)
ORDER BY ${SORT_COLUMN} ${SORT_TYPE}
ORDER BY REG_DTM DESC, ESTM_REQ_NO DESC
OFFSET (${ROW_COUNT} * ${PAGE_NO}) - ${ROW_COUNT} ROWS FETCH FIRST ${ROW_COUNT} ROWS ONLY

mapper.update:
/* DispPrdMngEstmReqLst.updateProductEstimateReqDtl */
UPDATE TB_PR_DISP_ESTM_REQ_DTLS
SET

    CPRTCP_ID = #{CPRTCP_ID}
    , ESTM_REQDT = #{ESTM_REQDT}


    DLV_LT = #{DLV_LT}
    , MIN_ORD_QTY = #{MIN_ORD_QTY}
    , MULT_ODR_UNIT = #{MULT_ODR_UNIT}
    , ESTM_MEMO = #{ESTM_MEMO}
    , CPRTCP_EPRC = #{CPRTCP_EPRC}
    , ESTM_SBM_AMT = #{ESTM_SBM_AMT}
    , ESTM_CMPL_DT = #{ESTM_CMPL_DT}
    , ESTM_VLD_DT = #{ESTM_VLD_DT}
    , DOC_REG_ID = #{DOC_REG_ID}


    EXCEPT_END_RSN = #{EXCEPT_END_RSN}

, UPDPSN_ID = #{OPRTR_ID}
, UPD_DTM = SYSDATE
WHERE CO_CD = #{CO_CD}
AND ESTM_REQ_NO = #{ESTM_REQ_NO}

mapper.insert:
/* DispPrdMngEstmReqLst.insertProductEstimateStatus */
INSERT INTO TB_RD_RND_DISP_ESTM_STATS_DTLS (
                                         CO_CD
                                       , ESTM_REQ_NO
                                       , SEQ
                                       , ESTM_REQ_PROC_STATS_CD
                                       , MALL_SPR_CD
                                       , REGPSN_ID
                                       , REG_DTM
                                       , UPDPSN_ID
                                       , UPD_DTM
) VALUES (
             #{CO_CD}
         , #{ESTM_REQ_NO}
         , (SELECT NVL(MAX(SEQ), 0) + 1 FROM TB_RD_RND_DISP_ESTM_STATS_DTLS WHERE CO_CD = #{CO_CD} AND ESTM_REQ_NO = #{ESTM_REQ_NO})
         , #{ESTM_REQ_PROC_STATS_CD}
         , #{MALL_SPR_CD}
         , #{OPRTR_ID}
         , SYSDATE
         , #{OPRTR_ID}
         , SYSDATE
         )

mapper.update:
UPDATE	TB_RD_REQ_CPRTCP_DISP_ESTM_INFO
SET		ESTM_INFO_STATS_CD = #{ESTM_INFO_STATS_CD}

			, PRD_ID = #{RND_REQ_PRD_ID}

WHERE	CO_CD = #{CO_CD}
AND		ESTM_REQ_NO = #{ESTM_REQ_NO}

UPDATE	TB_RD_RND_DISP_ESTM_REQ_DTLS
SET		ESTM_REQDT = TO_CHAR(SYSDATE, 'YYYYMMDD')
WHERE	ESTM_REQ_NO = #{ESTM_REQ_NO}
AND		DISP_GRP_ID = #{DISP_GRP_ID}
AND		PRD_CLCD = #{PRD_CLCD}

mapper.select:
SELECT  ESTM_REQ_PROC_STATS_CD
FROM    (
		SELECT  S1.ESTM_REQ_PROC_STATS_CD
		FROM    TB_RD_RND_DISP_ESTM_STATS_DTLS S1
		WHERE   S1.CO_CD = #{CO_CD}
		AND     S1.ESTM_REQ_NO = #{ESTM_REQ_NO}
		ORDER BY SEQ DESC
		)
WHERE   ROWNUM = 1

mapper.update:
/* DispPrdMngEstmReqLst.updateProductEstimateReqAtflDtl */
UPDATE TB_CO_ATFL_DTLS
SET ETC_FDS_1 = 'C4'
WHERE DOC_REG_ID = #{DOC_REG_ID}

mapper.delete:
/* DispPrdMngEstmReqLst.deleteProductEstimateLayoutReset */
DELETE TB_CO_UI_PSNL_DTLS
WHERE CO_CD = #{CO_CD}
  AND OPRTR_ID = #{OPRTR_ID}
  AND MNU_SEQ = '12'

mapper.select:
/* DispPrdMngEstmReqLst.selectNewProductReqMemo */
SELECT '견적협력사:' || T2.CPRTCP_KOR_NM || ';' || '배송L/T:' || T1.DLV_LT || ';' || 'MOQ:' || T1.MIN_ORD_QTY || ';' || '배수주문단위:' || T1.MULT_ODR_UNIT || ';' || '견적메모:' || T1.ESTM_MEMO || ';' || '협력사견적가:' || T1.CPRTCP_EPRC || ';' || '견적제출가:' || T1.ESTM_SBM_AMT || ';' AS MEMO
FROM TB_PR_DISP_ESTM_REQ_DTLS T1
   , TB_CP_CPRTCP_BASIS T2
WHERE T1.CO_CD = T2.CO_CD
  AND T1.CPRTCP_ID = T2.CPRTCP_ID
  AND T1.CO_CD = #{CO_CD}
  AND T1.ESTM_REQ_NO = #{ESTM_REQ_NO}

mapper.update:
/* DispPrdMngEstmReqLst.updateNewPrdReqProc */
UPDATE TB_PR_NEW_PRD_REQ_PROC
SET MRO_REQ_NO = #{MRO_REQ_NO}	/* MRO요청번호 */
  , UPDPSN_ID = #{OPRTR_ID}	/* 수정자ID   */
  , UPD_DTM = SYSDATE			/* 수정일시    */
WHERE CO_CD = #{CO_CD}
  AND NEW_PRD_REQ_NO = #{NEW_PRD_REQ_NO}

