mapper.select:
SELECT  MAX(ESTM_REQ_PROC_STATS_CD) AS ESTM_REQ_PROC_STATS_CD
FROM    TB_RD_RND_DISP_ESTM_STATS_DTLS
WHERE   ESTM_REQ_NO = #{ESTM_REQ_NO}
